#include <vector>
using namespace std;

class Solution {
        const long long MOD = 1e9 + 7;
        long long countPairs(int index, int arr1Value, int arr2Value , vector<vector<vector<long long>>> &dp , vector<int> &values) {
        int n = dp.size();
        if (index >= n) return 1;
        if (dp[index][arr1Value][arr2Value] != -1) return dp[index][arr1Value][arr2Value];
        long long result = 0;  
        for (int i = arr1Value; i <= values[index]; i++) {
            for (int j = arr2Value; j >= 0; --j) {
                if (i + j == values[index]) {
                    result += countPairs(index + 1, i, j , dp , values);
                    result %= MOD;
                }
            }
        }
        return dp[index][arr1Value][arr2Value] = result;
    }

public:
    long long countOfPairs(vector<int>& values) {
        int n = values.size();
        vector<vector<vector<long long>>> dp(n, vector<vector<long long>>(51, vector<long long>(51, -1)));
        int ans = countPairs(0, 0, values[0] , dp , values);
        return ans;
    }
};
