class Solution {
public:
    int dp[2005][52][52];
    int mod=1e9+7;
    int rec(vector<int>&nums,int ind,int incPrev,int decPrev){
        if(ind>=nums.size()){
            return 1;
        }
        if(dp[ind][incPrev][decPrev]!=-1) return dp[ind][incPrev][decPrev];
        int ans=0;
        for(int i=0;i<=nums[ind];i++){
            if(i<incPrev){
                continue;
            }

            int dec=nums[ind]-i;

            if(dec>decPrev){
                continue;
            }
            ans=(ans+rec(nums,ind+1,i,dec))%mod;       
        }
        return dp[ind][incPrev][decPrev]=(ans)%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return rec(nums,0,0,51)%mod;
    }
};
