class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        m = 10 ** 9 + 7

        @cache
        def dfs(index, prevNum1):
            if index == len(nums):
                return 1
            res = 0
            for i in range(prevNum1, nums[index] + 1):
                prevNum2 = 0
                if index > 0:
                    prevNum2 = nums[index - 1] - prevNum1
                curNum2 = nums[index] - i
                if index == 0 or curNum2 <= prevNum2:
                    res += dfs(index + 1, i)
                    res %= m 
            return res 
        return dfs(0, 0)
