class Solution {
public:
    long long mod=1e9+7;
    int solve(int index,int v1,int v2,vector<int> &nums,vector<vector<vector<long long>>> &dp)
    {
        if(index==nums.size())return 1;
        if(dp[index][v1][v2]!=-1)return dp[index][v1][v2];
        long long ans=0;
        for(int i=v1;i<=nums[index];i++)
        {
            for(int j=v2;j>=0;j--)
            {
                if((i+j)==nums[index])
                {
                    ans=(ans%mod+solve(index+1,i,j,nums,dp)%mod)%mod;
                }
            }
        }
        return dp[index][v1][v2]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        vector<int> asc=nums,desc=nums;
        int n=nums.size();
//         sort(asc.begin(),asc.end());
//         sort(desc.rbegin(),desc.rend());
        vector<vector<vector<long long>>> dp(n+1, vector<vector<long long>>(55, vector<long long>(55, -1)));
        return solve(0,0,nums[0],nums,dp)%mod;

    }
};
