class Solution:

    def __init__(self):
        self.mod = 1e9 + 7

    def countOfPairs(self, nums: List[int]) -> int:

        dp = {}
        def dfs(index, prevArr1):
            if index == len(nums):
                return 1

            if (index, prevArr1) in dp:
                return dp[(index,prevArr1)]

            count = 0
            for i in range(nums[index] + 1):
                arr1, arr2 = i, nums[index] - i

                if prevArr1 is not None:
                    if arr1 < prevArr1:
                        continue
                    if arr2 > (nums[index - 1] - prevArr1):
                        continue

                count = (count + (dfs(index + 1, arr1) % self.mod)) % self.mod

            dp[(index, prevArr1)] = int(count % self.mod)
            return dp[(index, prevArr1)]

        return dfs(0, None)
