class Solution {
public:

    int mod=1e9+7;

    long solve(int idx, int prev1, int prev2, vector<vector<vector<long>>>& dp, vector<int>& nums){

        if(idx<0){
            return 1;
        }

        if(dp[idx][prev1][prev2]!=-1) return dp[idx][prev1][prev2];


        long res=0;

        for(int i=0;i<=prev1;i++){
            if(nums[idx]-i >= prev2){
                res+= (solve(idx-1, i, nums[idx]-i, dp, nums)%mod);
                res=res%mod;
            }
        }

        return dp[idx][prev1][prev2]= (res%mod);
    }

    int countOfPairs(vector<int>& nums) {
        int maxi=nums[0];
        for(auto it:nums){
            maxi=max(maxi,it);
        }
        int n=nums.size();
        vector<vector<vector<long>>> dp(n, vector<vector<long>>(maxi+1, vector<long>(maxi+1,-1)));

        int res=0;

        for(int i=0;i<=nums[n-1];i++){
                res+= (solve(n-2, i, nums[n-1]-i, dp, nums)%mod);
                res=res%mod;
        }

        return res;

    }
};
