class Solution {
public:
    int mod=1e9+7;
    int dp[2000][51][51];
    int f(int ind,int prev1,int prev2,vector<int>&arr){
        if(ind==arr.size())
        return 1;
        if(dp[ind][prev1][prev2]!=-1)
        return dp[ind][prev1][prev2];
        int ans=0;
        for(int i=0;i<=arr[ind];i++){
            int a1=i,a2=arr[ind]-i;
            if(a1>=prev1 && a2<=prev2){
            ans+=(f(ind+1,a1,a2,arr));
            ans%=mod;
            }
        }
        return dp[ind][prev1][prev2]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof dp);
        return f(0,0,50,nums);
    }
};
