class Solution {
    int M=1e9+7;
    int f(int ind,int l,int r,vector<int>& nums,vector<vector<vector<int>>>&dp){
        if(ind==nums.size())return 1;
        if(dp[ind][l][r]!=-1)return dp[ind][l][r];
        int x=nums[ind];
        int ans=0;
        for(int i=l;i<=x;i++){
            if(x-i<=r)ans=(ans+f(ind+1,i,x-i,nums,dp))%M;
        }
        return dp[ind][l][r]=ans%M;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(51,vector<int>(51,-1)));
        return f(0,0,50,nums,dp);
    }
};
