class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n=len(nums)
        md=10**9+7
        dp=[[[-1]*51 for _ in range(51)] for __ in range(n+1)]
        def fun(i,p1,p2):
            if i>=n:
                return 1
            if dp[i][p1][p2]!=-1:
                return dp[i][p1][p2]
            ans=0
            for a in range(p1,nums[i]+1):
                if nums[i]-a<=p2:
                    ans+=fun(i+1,a,nums[i]-a)
            dp[i][p1][p2]=ans%md
            return dp[i][p1][p2]
        return fun(0,0,50)
