class Solution {
public:
    const int mod=1e9+7;
    int dp[51][51][2002];
    int f(int pval1,int pval2,int i,vector<int> &nums)
    {
        int n=nums.size();
        if(i>=n) return 1;
        if(dp[pval1][pval2][i]!=-1) return dp[pval1][pval2][i];
        int res=0;
        for(int c1=pval1;c1<=nums[i];c1++)
        {
            int c2=nums[i]-c1;
            if(c2<=pval2)
            {
                res=(res+f(c1,c2,i+1,nums))%mod;
            }
        }
        return dp[pval1][pval2][i]=res;

    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof dp);
        return f(0,50,0,nums);
    }
};
