class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        H = 50
        H = max(nums)+1
        modulo = 10**9 + 7
        dp = [[0 for i in range(H)] for j in range(n+1)]
        #dp = np.zeros((n+1, H), dtype=int)
        for h in range(nums[n-1]+1):
            dp[n-1][h] = 1
        for i in reversed(range(n-1)):
            for h in range(nums[i]+1):
                for ht in range(h, nums[i+1]+1):
                    if (i+1<n) and (nums[i]-h < nums[i+1]-ht):
                        continue
                    dp[i][h] = (dp[i][h] + dp[i+1][ht]) % modulo
        ans = 0
        for h in range(H):
            ans = (ans + dp[0][h]) % modulo
        #print(dp)
        return ans
