class Solution {
public:
    int countOfPairs(vector<int>& a) {
        long long modulo = 1e9 + 7;
        int n = a.size();
        vector<vector<long long>> f(n, vector<long long>(51, 0));
        for (int i = 0; i <= a[0]; ++i)
        {
            f[0][i] = 1;
        }

        for (int i = 1; i < n; ++i)
        {
            for (int j = 0; j <= a[i]; ++j)
            {
                int kLimit = min(j, a[i - 1] - a[i] + j);
                for (int k = 0; k <= kLimit; ++k)
                {
                    f[i][j] = (f[i][j] + f[i - 1][k]) % modulo;
                }
            }
        }

        int ans = 0;
        for (int i = 0; i <= a[n - 1]; ++i)
        {
            ans = (ans + f[n - 1][i]) % modulo;
        }

        return ans;
    }
};
