class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int first = nums[0];
        int result =0;
        map<int,pair<int,pair<int,int>>> graph;
        for(long long i=0;i<=first*1ll;i++){
            long long diff = nums[0]-i;
            result+=solve(i,1,diff, nums, graph);
            result%=MOD;
        }
        return result;
    }
    int MOD = 1e9+7;
    int solve(int prev_1, int index, int prev_2, vector<int>&nums, map<int,pair<int,pair<int,int>>>&graph){
        int len = nums.size();
        if(index==len){
            return 1;
        }
        if(graph.find(prev_1)!=graph.end()){
            pair<int,pair<int,int>> inner = graph[prev_1];
            if(inner.first==prev_2){
                if(inner.second.first==index) {
                    return inner.second.second;
                }
            }
        }
        int ans =0;
        for(int i=prev_1;i<=nums[index];i++){
            if(i>=prev_1){
                int second = nums[index]-i;
                if(second<=prev_2){
                    ans += solve(i, index+1, second, nums, graph);
                    ans%=MOD;
                }
            }
        }
        graph[prev_1]=make_pair(prev_2, make_pair(index,ans));
        return ans;

    }
};
