class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        base = int(1e9 + 7)
        prev = {}
        for arr1First in range(nums[0]+1):
            arr2First = nums[0] - arr1First
            prev[(arr1First, arr2First)] = 1

        for i in range(1, len(nums)):
            num = nums[i]
            curr = {}
            for arr1Next in range(num+1):
                arr2Next = num - arr1Next
                currCount = 0
                for (arr1Prev, arr2Prev), count in prev.items():
                    if arr1Prev <= arr1Next and arr2Prev >= arr2Next:
                        currCount = (currCount + count) % base

                curr[(arr1Next, arr2Next)] = currCount

            prev = curr

        res = 0
        for count in prev.values():
            res = (res + count) % base

        return res
