class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        N = len(nums)
        memo = [[] for _ in range(N)]
        memo[-1] = [1]*(nums[-1]+1)

        for i in range(N-2,-1,-1):
            # now we can take any of values in arr1 from 0 to x
            for k in range(nums[i]+1):
                # suppose arr1 has value k
                k2 = nums[i]-k
                m = 0
                # how many ways it is possible ? look forward
                # arr1 is non-decreasing , so we can take any of non-decreasing values
                for kNd in range(k, len(memo[i+1])):
                    kNi2 = nums[i+1]-kNd
                    if kNi2 <= k2:
                        m += memo[i+1][kNd]

                m %= 1000000007
                memo[i].append(m) # at position k

        return sum(memo[0])%1000000007
