class Solution {
public:
int MOD=1e9+7;
long long solve(const vector<int>& values, int index, int val1, int val2, vector<vector<vector<long long>>>&dp) {
        if (index >= values.size()) return 1;  
        if (dp[index][val1][val2] != -1) return dp[index][val1][val2]; 

        long long result = 0;
        for (int i = val1; i <= values[index]; ++i) {
            for (int j = val2; j >= 0; --j) {
                if (i + j == values[index]) {
                    result = (result + solve(values, index + 1, i, j, dp)) % MOD;
                }
            }
        }
        dp[index][val1][val2] = result;  
        return result;
    }
    int countOfPairs(vector<int>& nums) {
             int size = nums.size();

        vector<vector<vector<long long>>> dp(size, vector<vector<long long>>(51, vector<long long>(51, -1)));

        return solve(nums, 0, 0, nums[0], dp);
    }
};
