const int MOD = 1e9 + 7;
class Solution
{
public:
	int countOfPairs(vector<int> &a)
	{
		int n = sz(a);
		int dp[2010][51];
		memset(dp, 0, sizeof(dp));

		loop(i, 0, a[0] + 1) dp[0][i] = 1;

		loop(i, 1, n)
		{
			loop(j, 0, a[i] + 1)
			{
				int t = a[i] - j;
				if (t >= 0)
				{
					for (int k = 0; k <= j; ++k)
					{
						if (t > a[i - 1] - k)
							continue;
						dp[i][j] = (dp[i][j] + dp[i - 1][k]) % MOD;
					}
				}
			}
		}
		int ans = 0;
		loop(i, 0, a[n - 1] + 1) ans = (ans + dp[n - 1][i]) % MOD;
		return ans;
	}
};
