class Solution {
public:
    int dp[2005][55][55];
    int mod = 1e9 + 7;
    int rec(int idx, int a, int b, vector<int>& nums){
        if(idx >= nums.size())return 1;
        if(dp[idx][a][b] != -1)return dp[idx][a][b];

        int current = 0;
        for(int i=nums[idx]; i>=a; i--){
            int pb = nums[idx] - i;
            if(pb < 0 || pb > b)break;
            current = ((current%mod ) + (rec(idx+1, i, pb, nums)%mod))%mod;
        }

        return dp[idx][a][b] = current;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof dp);
        return rec(0, 0, 52, nums);
    }
};
