class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)

        dp = [[0] * (nums[i] + 1) for i in range(n)]

        for x in range(nums[0] + 1):
            dp[0][x] = 1

        for i in range(1, n):
            for x in range(nums[i] + 1):
                arr2_i = nums[i] - x
                for prev_x in range(nums[i-1] + 1):
                    if prev_x <= x and (nums[i-1] - prev_x) >= arr2_i:
                        dp[i][x] = (dp[i][x] + dp[i-1][prev_x]) % MOD

        total = sum(dp[n-1]) % MOD
        return total
