class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        @cache
        def count(idx, prev):
            if idx == len(nums):
                return 1

            res = 0 
            lower_bound = 0 if idx == 0 else max(nums[idx] - nums[idx-1] + prev, 0, prev)
            upper_bound = nums[idx]
            for n in range(lower_bound, upper_bound + 1):
                res += count(idx + 1, n)
            return res

        return count(0, 0) % (pow(10, 9) + 7)
