class Solution {
    long mod = (long) (1e9 + 7);

    static class T {
        int a, b;
        long v;
        T(int a, int b, long v) {
            this.a = a;
            this.b = b;
            this.v = v;
        }

        public String toString() {
            return "(" + a + ", " + b + ", "  + v + ")";
        }
    }

    public int countOfPairs(int[] nums) {
        long count = 0;
        int n = nums.length;

        ArrayList<ArrayList<T>> dp = new ArrayList<ArrayList<T>>();

        for (int i = 0; i < n; i++) {
            dp.add(new ArrayList<T>());

            for (int j = 0; j <= nums[i]; j++) {
                dp.get(i).add(new T(j, nums[i]-j, (i == 0) ? 1 : 0));
            }
        }

        // System.out.println(dp);


        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= nums[i]; j++) {
                int a2 = j;
                int b2 = nums[i] - a2;

                for (int k = 0; k <= nums[i-1]; k++) {
                    int a1 = k;
                    int b1 = nums[i-1]-a1;

                    if (a2 >= a1) {
                        if (b2 <= b1) {
                            dp.get(i).get(j).v += dp.get(i-1).get(k).v;
                            dp.get(i).get(j).v %= mod;
                        }
                    }

                }

            }
        }



        for (int i = 0; i < dp.get(dp.size()-1).size(); i++) {
            count += dp.get(dp.size() - 1).get(i).v;
            count %= mod;
        }
        return (int) count;
    }
}
