class Solution {
    private static final int MOD = 1_000_000_007;
    private int[][][] dp;

    public int findPairs(int[] nums, int idx, int prev1, int prev2) {
        if (idx >= nums.length) {
            return 1;
        }
        if (dp[idx][prev1][prev2] != -1) {
            return dp[idx][prev1][prev2];
        }
        int ans = 0;
        for (int i = 0; i <= nums[idx]; i++) {
            int arr1_val = i;
            int arr2_val = nums[idx] - i;
            if (arr1_val >= prev1 && arr2_val <= prev2) {
                ans = (ans + findPairs(nums, idx + 1, arr1_val, arr2_val)) % MOD;
            }
        }
        dp[idx][prev1][prev2] = ans;
        return ans;
    }
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        dp = new int[n][51][51];  
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 51; j++) {
                for (int k = 0; k < 51; k++) {
                    dp[i][j][k] = -1;
                }
            }
        }
        return findPairs(nums, 0, 0, 50);
    }
}
