class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(101, 0));

        // Initialize the base case for i = 0
        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;  // arr1[0] = j, arr2[0] = nums[0] - j
        }

        // Fill dp array
        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= nums[i]; ++j) {
                int arr2_i = nums[i] - j;
                if (arr2_i >= 0) {
                    for (int k = 0; k <= j; ++k) {
                        if (nums[i-1] - k >= arr2_i) {
                            dp[i][j] = (dp[i][j] + dp[i-1][k]) % MOD;
                        }
                    }
                }
            }
        }

        // Compute the final result
        int result = 0;
        for (int j = 0; j <= nums[n-1]; ++j) {
            result = (result + dp[n-1][j]) % MOD;
        }

        return result;
    }
};
