import java.util.*;

class Solution {

     int MOD = 1000000007;
  HashMap<String, Integer> hm;

    public int countOfPairs(int[] nums) {
        hm = new HashMap<>();
        return rec(0, nums, -1, -1);  
    }

    private int rec(int i, int[] nums, int arr1, int arr2) {
        if (i == nums.length) {
            return 1; 
        }


        String hash = i + "," + arr1 + "," + arr2;
        if (hm.containsKey(hash)) {
            return hm.get(hash);
        }

        int count = 0;
        for (int num1 = 0; num1 <= nums[i]; num1++) {
            int b = nums[i] - num1;

            if ((arr1 == -1 || arr1 <= num1) && (arr2 == -1 || arr2 >= b)) {
                count = (count + rec(i + 1, nums, num1, b)) % MOD;
            }
        }

        hm.put(hash, count);  
        return count;
    }


}
