#define ll long long int
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int size = nums.size();
        vector<vector<vector<ll>>> dp(size, vector<vector<ll>>(51, vector<ll>(51, -1)));
        ll mod = 1e9 + 7;
        return solve(0, 0, nums[0], nums, dp, mod);
    }

private:
    ll solve(int ind, int v1, int v2, vector<int>& nums, vector<vector<vector<ll>>>& dp, ll mod) {
        if (ind >= nums.size()) return 1;
        if (dp[ind][v1][v2] != -1) return dp[ind][v1][v2];

        ll res = 0;
        for (int i = v1; i <= nums[ind]; i++) {
            for (int j = v2; j >= 0; j--) {
                if (i + j == nums[ind]) {
                    res = (res + solve(ind + 1, i, j, nums, dp, mod)) % mod;
                }
            }
        }

        return dp[ind][v1][v2] = res;
    }
};
