class Solution {
public:

const int M = 1e9 + 7;

long solve(vector<int>& v, int idx, int x, int y, vector<vector<vector<long>>>& dp) {
    if (idx >= v.size()) return 1;
    if (dp[idx][x][y] != -1) return dp[idx][x][y];

    long res = 0;
    for (int i = x; i <= v[idx]; i++) {
        for (int j = y; j >= 0; j--) {
            if (i + j == v[idx]) {
                res = (res + solve(v, idx + 1, i, j, dp)) % M;
            }
        }
    }

    return dp[idx][x][y] = res;
}
