class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        dp = [[[-1]*51 for _ in range(51)] for _ in range(n)]
        md = int(10**9)+7
        def rec(a1,a2,i):
            if i == n:
                return 1
            if dp[i][a1][a2] != -1:
                return dp[i][a1][a2]
            dp[i][a1][a2]  = 0
            for v in range(51):
                if v > nums[i]:
                    continue
                if a1 > v or a2 < (nums[i]-v):
                    if i != 0:
                        continue
                dp[i][a1][a2]  += rec(v,nums[i]-v,i+1)
                dp[i][a1][a2]%=md
            return dp[i][a1][a2]
        return rec(0,0,0)%md
