class Solution {
public:
    int solve(vector<int>& nums,int ind,int arr1,int arr2,vector<vector<vector<int>>>& dp){
        int modulo = 1e9+7;
        int ways = 0;
        if(dp[ind][arr1][arr2] != -1){
            return dp[ind][arr1][arr2];
        }
        for(int i=0;i<=nums[ind];i++){
            if(i>(arr1-1) && (arr1-1) != -1){
                continue;
            }
            if((nums[ind]-i)<(arr2-1) && (arr2-1) != -1){
                continue;
            }
            if(ind == 0)  ways += 1;
            else ways = (ways+solve(nums,ind-1,i+1,nums[ind]-i+1,dp))%modulo;
        }
        return dp[ind][arr1][arr2] = ways;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int maxi = INT_MIN;
        for(auto it : nums){
            maxi = max(maxi,it);
        }
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(maxi+2,vector<int>(maxi+2,-1)));
        return solve(nums,n-1,0,0,dp);
    }
};
