class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        M = 10 ** 9 + 7
        n = len(nums)
        m = max(nums) + 1
        dp = [[0] * m for _ in range(n)]
        for num in range(nums[0] + 1):
            dp[0][num] = 1

        for i in range(1, n):
            for j in range(nums[i] + 1):
                for k in range(j + 1):
                    if nums[i-1] - k >= nums[i] - j:
                        dp[i][j] += dp[i-1][k]
                        dp[i][j] %= M
        return sum(dp[-1]) % M
