class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9+7;
        vector<int> prev(51, 0);
        for (int i = 0; i <= 50; i++) {
            if (i <= nums[0]) prev[i] = 1;
        }

        for (int i = 1; i < nums.size(); i++) {
            vector<int> temp(51, 0);
            for (int j = 0; j <= nums[i]; j++) {
                int a = nums[i] - j;
                for (int k = 0; k <= j; k++) {
                    if (nums[i-1] - k >= a) {
                        temp[j] += prev[k];
                        temp[j] %= MOD;
                    }
                }
            }
            prev = temp;
        }
        int res = 0;
        for (int i : prev) {
            res += i;
            res %= MOD;
        }
        return res;
    }
};
