class Solution {
public:

    int mod = 1e9+7;

    int calc(int ind, int mini, vector<int>&nums, vector<vector<int>>&dp){
        if(ind == nums.size()) return 1;

        if(dp[ind][mini] != -1) return dp[ind][mini];

        int start = max(mini,nums[ind]-nums[ind-1]+mini);

        int temp = 0;
        for(int i=start;nums[ind]-i >= 0;i++){
            temp += calc(ind+1,i,nums,dp);
            temp %= mod;
        }
        return dp[ind][mini] = temp;
    }

    int countOfPairs(vector<int>& nums) {
        int n = *max_element(nums.begin(),nums.end());
        vector<vector<int>>dp(nums.size(),vector<int>(n+1,-1));
        int ans=0;
        for(int i=0;i<=nums[0];i++){
            ans += calc(1,i,nums,dp);
            ans %= mod;
        }
        return ans;
    }
};
