typedef long long int ll;
const ll mod=1e9+7;

class Solution {
    vector<int> nums;
    int n;
    ll ans;
    vector<vector<vector<ll>>> dp;

    ll func(int ind,int prev1,int prev2){
        // cout<<ind<<" "<<prev1<<" "<<prev2<<endl;
        if(ind==n){
            return dp[ind][prev1][prev2]=1;
        }

        if(dp[ind][prev1][prev2]!=-1){
            return dp[ind][prev1][prev2];
        }


        int low=0;
        int high=nums[ind];
        ll ans=0;

        while(high!=-1){
            if(low>=prev1 && high<=prev2){
                ans+=func(ind+1,low,high);
                ans%=mod;
            }
            low++;
            high--;
        }

        return dp[ind][prev1][prev2]=ans; 

    }

public:
    int countOfPairs(vector<int>& nums) {
        ans=0;
        this->nums=nums;
        n=nums.size();
        dp.resize(n+2,vector<vector<ll>>(52,vector<ll>(52,-1)));
        return func(0,0,nums[0]);
        // return ans;
    }
};
