class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 1e9 + 7
        @cache
        def dfs(a, b, i):
            if i >= len(nums) - 1:
                return 1

            out = 0
            for aVal in range(a, nums[i + 1] + 1):
                bVal = nums[i + 1] - aVal
                if bVal <= b and bVal >= 0:
                    out = (out + dfs(aVal, bVal, i + 1)) % MOD

            return out % MOD

        out = 0
        for a in range(nums[0] + 1):
            out = (out + dfs(a, nums[0] - a, 0)) % MOD
        return int(out)
