const int m=1e9+7;
class Solution {
public:
    vector<vector<int>> dp;
    int solver(int i, int p,vector<int>& nums)
    {
        if(i>=nums.size())
            return 1;
        if(dp[i][p]!=-1)
            return dp[i][p];
        int bp=50;
        if(i>0)
            bp=nums[i-1]-p;
        int ans=0;
        for(int j=p;j<=nums[i];j++)
        {
            if((nums[i]-j)<=bp)
                ans+=solver(i+1,j,nums);
            ans%=m;
        }
        ans%=m;
        return dp[i][p]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int ans=0;
        dp.assign(nums.size()+1,vector<int>(51,-1));
        return solver(0,0,nums)%m;
    }
};
