class Solution {
public:
    int mod = 1e9+7;
    int dp[2001][51];
    int find(int i,vector<int> &nums,int pre1,int pre2) {
        if(i == nums.size()) return 1;
        if(dp[i][pre1] != -1) return dp[i][pre1];
        int ans =0;
        for(int x =0; x<=50; x++) {
            if(x >= pre1 && nums[i]-x <= pre2 && 0 <= nums[i]-x) {
                ans  =  (ans+ find(i+1,nums,x,nums[i]-x))%mod;;
            }
        }
        return dp[i][pre1] = ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return find(0,nums,0,50);
    }
};
