class Solution {
public:
    int f(vector<int>& nums, int i,int p1,int p2,vector<vector<vector<int>>> &dp){
        if(i>=nums.size()){
            return 1;
        }
        if(dp[i][p1][p2]!=-1)
        {
            return dp[i][p1][p2];
        }
        int ans=0;
        int curr=nums[i];
        for(int k=0;k<=curr;k++){
            if(k>=p1&&(curr-k)<=p2){
                ans=(ans+f(nums,i+1,k,curr-k,dp))%1000000007;
            }
        }
        return dp[i][p1][p2]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(51,vector<int>(51,-1)));
        int ans=f(nums,0,0,50,dp);
        return ans;
    }
};
