import java.util.*;

class Solution {
    public int countOfPairs(int[] nums) {
        long[][][] dp = new long[nums.length + 1][51][51];
        for (long[][] arr2D : dp) {
            for (long[] arr1D : arr2D) {
                Arrays.fill(arr1D, -1);
            }
        }

        return (int)(solve(nums, 0, 0, 50, dp));
    }
    public long solve(int[] nums, int idx, int j, int k, long[][][] dp) {
        long count = 0;
        if (idx >= nums.length) return 1;
        if (dp[idx][j][k] != -1) return dp[idx][j][k];
        long mod = (long)1e9+7;

        for (int i = 0; i <= nums[idx]; i++) {
            if (i >= j && nums[idx] - i <= k) {
                count = (count + solve(nums, idx + 1, i, nums[idx] - i, dp))%mod;
            }
        }
        dp[idx][j][k] = count;
        return dp[idx][j][k];
    }
}
