const int m = 1e9+7;

class Solution {
    vector <vector <int>> dp;
public:

    // making increasing
    int countPairs(vector <int> &nums, int idx, int prev){
        int n = nums.size();
        if( idx == n )
            return 1;
        if( idx > n ) 
            return 0;
        if( dp[idx][prev] != -1 )
            return dp[idx][prev];

        int ans = 0;
        if( idx == 0 ){
            for( int i = 0 ; i <= nums[idx] ; i++){
                ans = (ans%m + countPairs(nums, idx+1, i)%m)%m;
            }
            return dp[idx][prev] = ans;
        }

        int prevDecreasing = nums[idx-1] - prev;
        if( prevDecreasing < 0 ) 
            return dp[idx][prev] = 0;
        for( int num = prev ; num <= nums[idx] ; num++ ){
            if( nums[idx]-num > prevDecreasing ) continue;
            ans = (ans%m + countPairs(nums, idx+1, num)%m)%m;
        }

        return dp[idx][prev] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp.resize(n+1, vector<int> (51,-1));
        return countPairs(nums,0,6);
    }
};
