class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def solve(index,a,b):
            if index==len(nums):
                return 1
            ans=0
            for i in range(nums[index]+1):
                first=i
                second=nums[index]-first
                if (first>=a or a==-1) and (second<=b or b==-1):
                    ans+=solve(index+1,first,second)
            return ans


        total=solve(0,-1,-1)

        return total%(10**9+7)
