class Solution {
public:
int dp[2001][52];
int c=1e9+7;
int func(int ind,int p1,vector<int> &nums){

    if(ind==nums.size())return 1;
  if(dp[ind][p1]!=-1)return dp[ind][p1];
    int p2;
    if(p1==51){p1=-1;p2=60;}

    else p2=nums[ind-1]-p1;
    int ans=0;
    for(int i=0;i<=nums[ind];i++){
        int x=i;int y=nums[ind]-x;
        if(x>=p1&&y<=p2)ans = (ans + func(ind+1,x,nums))%c;
    }
if(p1==-1)p1=51;
    return dp[ind][p1]=ans;
}
    int countOfPairs(vector<int>& nums) {
        for(int i=0;i<nums.size();i++){
            for(int j=0;j<=51;j++)dp[i][j]=-1;
        }

        return func(0,51,nums);
    }
};
