class Solution {
    using ll=long long;
public:
    ll help(vector<int>&nums,int i,int pre1,int pre2,vector<vector<ll>>&dp){
         ll mod=1e9 + 7;
          if(i==nums.size())return 1;
        //   if(dp[i][pre1][pre2]!=-1)return dp[i][pre1][pre2];
         if(dp[i][pre1]!=-1)return dp[i][pre1]%mod;

           ll ans=0;
         for(int k=0;k<=nums[i];k++){
            int n1=k;
            int n2=nums[i]-k;
            if(n1>=pre1 && n2<=pre2){
            ans += help(nums,i+1,k,nums[i]-k,dp);
            ans=ans%mod;
            }
         }
         return  dp[i][pre1] = ans;
    }
    int countOfPairs(vector<int>& nums) {
         int n=nums.size();
         vector<vector<ll>>dp(n,vector<ll>(51,-1));
        // vector<vector<vector<long long>>> dp(n, vector<vector<long long>>(51, vector<long long>(51, -1)));
        // vector<vector<vector<ll>>>dp(nums.size(),vector<vector<ll>>(51,vector<ll>(51,-1)));
        ll ans= help(nums,0,0,10000,dp);
        // for(auto i:dp)cout<<i<<" ";
        return ans;
    }
};
