class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        # res = [0]
        n = len(nums)
        def solve(i, last):
            # print(i, last)
            if i == n:
                # res[0] += 1
                # print("caught")
                return 1
            if (i, last) in dp:
                return dp[(i, last)]
            cur = 0
            if last == None:
                for k in range(nums[i]+1):
                    # print(f'from {i} called {i+1, k}')
                    cur += solve(i+1, k)
            else:
                seclast = nums[i-1]-last
                # print(f'for index {i} seclast is {seclast}')
                # print(f'index {i}, last {last}, seclast {seclast}')
                for k in range(nums[i]+1):
                    if k >= last and nums[i]-k <= seclast:
                        # print(f'from {i}, called {i+1, k}')
                        cur += solve(i+1, k)
            dp[(i, last)] = cur
            return cur
        dp = {}
        res = solve(0, None)%(10**9+7)
        return res
