class Solution {
public:
    int MOD = 1e9+7;
    vector<vector<vector<int>>> dp;
    int func(int i,int prev_arr1,int prev_arr2,vector<int> &nums){
        // base case
        if (i == nums.size()) return 1;
        if(dp[i][prev_arr1][prev_arr2] != -1) return dp[i][prev_arr1][prev_arr2];

        int count = 0;
        for(int k=prev_arr1;k<=nums[i];k++){
            if(i == 0) prev_arr2 = nums[i] - k;

            if(i != 0 && prev_arr2 < nums[i] - k) continue;
            count = (count + func(i + 1, k, nums[i]-k , nums)) % MOD;
        }
        dp[i][prev_arr1][prev_arr2] = count;
        return count;
    }
    int countOfPairs(vector<int>& nums) {
        dp.resize(nums.size() ,vector<vector<int>>(51 , vector<int> (51,-1)));

        return func(0,0,nums[0],nums);
    }
};
