class Solution {
public:
    int MOD = 1e9 + 7;
    int find(int i, int prev1, int prev2, vector<int>& nums, int& ans,
             vector<vector<vector<int>>>& dp) {

        if (i == nums.size()) {
            return 1;
        }

        if (prev1 != INT_MIN && prev2 != INT_MAX && dp[i][prev1][prev2] != -1)
            return dp[i][prev1][prev2];

        int count = 0;
        for (int j = 0; j <= nums[i]; j++) {
            int a = j;
            int b = nums[i] - j;

            if (a >= prev1 && b <= prev2) {
                count += find(i + 1, a, b, nums, ans, dp);
                count = count % MOD;
            }
        }

        if (prev1 != INT_MIN && prev2 != INT_MAX)
            dp[i][prev1][prev2] = count % MOD;

        return count;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(
            n, vector<vector<int>>(52, vector<int>(52, -1)));
        int ans = find(0, INT_MIN, INT_MAX, nums, ans, dp);

        return ans;
    }
};
