class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        mod = 10**9+7
        @cache
        def f(i,r):
            ways = 0
            if i == len(nums):
                return 1
            for mk in range(r,nums[i]+1):
                if i > 0 :
                    if (nums[i-1] - r >= nums[i]-mk):
                        ways =  (ways+f(i+1,mk))%mod
                elif i == 0:
                    ways = (ways+f(i+1,mk))%mod

            return ways%mod


        ans = f(0,0)
        return ans%mod
