#include <vector>

class Solution {
public:
    static const int MOD = 1000000007;
    int dp[51][51][2001];
    int countOfPairs(std::vector<int>& nums) {
        int n = nums.size();
        for (int i = 0; i < 51; ++i) {
            for (int j = 0; j < 51; ++j) {
                for (int k = 0; k < 2001; ++k) {
                    dp[i][j][k] = -1;
                }
            }
        }
        return countPairs(nums, 0, 0, 50, n);
    }

    int countPairs(vector<int>& nums, int idx, int minA, int maxB, int n) {
        if (idx == n) return 1;
        if (dp[minA][maxB][idx] != -1) return dp[minA][maxB][idx];

        int count = 0;
        for (int a = minA; a <= nums[idx]; ++a) {
            int b = nums[idx] - a;
            if (b <= maxB) {
                count = (count + countPairs(nums, idx + 1, a, b, n)) % MOD;
            }
        }

        dp[minA][maxB][idx] = count;
        return count;
    }
};
