from functools import lru_cache
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9 + 7

        @lru_cache(None)
        def back(i: int, last_a: int, last_b: int) -> int:
            if i == len(nums):
                return 1 

            cnt = 0
            for a in range(nums[i] + 1):  
                b = nums[i] - a 
                if (i == 0 or last_a <= a) and (i == 0 or last_b >= b):
                    cnt += back(i + 1, a, b)
                    cnt %= mod  
            return cnt


        return back(0, -1, 101)
