class Solution {
    const static int MOD = 1e9+7;
    int dp[51][51][2001];
    long long int fn(int l, int r, int idx, int n, vector<int>& v){ // cout<<l<<" "<<r<<" "<<idx<<endl;
        if(idx==n)return 1;
        if(dp[l][r][idx]!=-1)return dp[l][r][idx];
        long long curr = 0;
        for(int i=l; i<=v[idx]; ++i){
            int j = v[idx]-i;
            if(j>r)continue;
            curr += fn(i,j,idx+1,n,v);
            curr %= MOD;
        }
        return dp[l][r][idx]=curr;
    }
public:
    int countOfPairs(vector<int>& v) {
        int n = v.size();
        memset(dp,-1,sizeof(dp));
        int ans = fn(0,v[0],0,n,v);
        return ans;
    }
};
