class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size(), m=1e9+7;

        vector<array<int,51>> dp(n+1);
        dp[0][0]=1;

        for (int i=0; i<n; ++i) {
            for (int j=0; j<=nums[i]; ++j) {
                for (int k=0; k<=j; ++k) {
                    if (i==0 || (nums[i-1]-k >= nums[i]-j)) {
                        dp[i+1][j] += dp[i][k];
                        dp[i+1][j] %= m;
                    }
                }
            }
        }

        int res=0;

        for (int i=0; i<=50; ++i) {
            res += dp[n][i];
            res %= m;
        }

        return res;
    }
};
