int MOD = 1e9 + 7;

class Solution {


private:
    vector<vector<vector<int>>> memo;
    int Solve(int idx, int prevA, int prevB, vector<int>& nums){

        if (idx == nums.size()) return 1;
        if (prevA > nums[idx] || prevB < 0) return 0;       
        if (memo[idx][prevA][prevB] != -1) return memo[idx][prevA][prevB];

        int target = nums[idx];
        int ans = 0;

        for(int i = prevA; i <= target; i++){
            int j = target - i;
            if (j <= prevB && j >= 0){
                ans = (ans + Solve(idx + 1, i, j, nums)) % MOD;
            }
        }

        return memo[idx][prevA][prevB] = ans;
    }

public:
    int countOfPairs(vector<int>& nums) {
        memo.resize(nums.size(), vector<vector<int>>(51, vector<int>(51, -1)));
        return Solve(0, 0, nums[0], nums);
    }
};
